import { CodeBlock } from "@vibe-web/ui/ai-elements/code-block";
import { Tool, ToolContent, ToolHeader } from "@vibe-web/ui/ai-elements/tool";
import type {
	EditUIToolInvocation,
} from "ai-sdk-claude-code";
import { EditIcon } from "lucide-react";

export function ClaudeCodeEditTool({
	invocation,
}: {
	invocation: EditUIToolInvocation;
}) {
	const hasParentToolUseId =
		invocation.state !== "input-streaming" &&
		invocation.callProviderMetadata?.claudeCode?.parentToolUseId;

	if (!invocation || invocation.state === "input-streaming") return null;
	const { input } = invocation;

	return (
		<Tool state={hasParentToolUseId ? undefined : invocation.state}>
			<ToolHeader icon={EditIcon}>
				<span className="truncate font-medium text-sm">
					Edit {input?.file_path}
				</span>
			</ToolHeader>
			<ToolContent>
				{input?.old_string ? (
					<div className="space-y-2 px-4 pb-4">
						<h4 className="font-medium text-muted-foreground text-xs uppercase tracking-wide">
							Old String
						</h4>
						<CodeBlock
							code={input.old_string}
							language="text"
							className="text-xs"
						/>
					</div>
				) : null}
				{input?.new_string ? (
					<div className="space-y-2 px-4 pb-4">
						<h4 className="font-medium text-muted-foreground text-xs uppercase tracking-wide">
							New String
						</h4>
						<CodeBlock
							code={input.new_string}
							language="text"
							className="text-xs"
						/>
					</div>
				) : null}
			</ToolContent>
		</Tool>
	);
}

import { tool, type UIToolInvocation } from "ai";
import { z } from "zod";

export const MultiEdit = tool({
	type: "provider-defined",
	id: "claude-code.MultiEdit",
	name: "MultiEdit",
	args: {},
	// Docs: https://docs.claude.com/en/docs/claude-code/sdk/sdk-typescript#multiedit
	inputSchema: z.object({
		/**
		 * The absolute path to the file to modify
		 */
		file_path: z.string(),
		/**
		 * Array of edit operations to perform sequentially
		 */
		edits: z.array(
			z.object({
				/**
				 * The text to replace
				 */
				old_string: z.string(),
				/**
				 * The text to replace it with
				 */
				new_string: z.string(),
				/**
				 * Replace all occurrences (default false)
				 */
				replace_all: z.boolean().optional(),
			}),
		),
	}),
	// Docs: https://docs.claude.com/en/docs/claude-code/sdk/sdk-typescript#multiedit-2
	outputSchema: z.string(),
});

export type MultiEditUIToolInvocation = UIToolInvocation<typeof MultiEdit>;

